package com.gtriangle.admin.bis.role.entity;import com.gtriangle.admin.db.annotation.PrimaryKeyField;import com.gtriangle.admin.db.annotation.Table;import java.io.Serializable;import java.util.Date;@Table(tableName = "t_b_role")public class Role implements Serializable {	private static final long serialVersionUID = 1L;	/** 	 * id	 */	@PrimaryKeyField	private Long id;	/**	 * 角色名称	 */	private String roleName;	/** 	 * 角色描述	 */	private String roleMemo;	/**	 * 是否是系统角色	 */	private Boolean isSystem;	/**	 * created 	 */	private Date created;	/** 	 * 更新时间	 */	private Date updated;	/**	 * 0:删除 1:可用	 */	private Boolean dataStatus;	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getRoleName() {		return roleName;	}	public void setRoleName(String roleName) {		this.roleName = roleName;	}	public String getRoleMemo() {		return roleMemo;	}	public void setRoleMemo(String roleMemo) {		this.roleMemo = roleMemo;	}	public Boolean getIsSystem() {		return isSystem;	}	public void setIsSystem(Boolean system) {		isSystem = system;	}	public Date getCreated() {		return created;	}	public void setCreated(Date created) {		this.created = created;	}	public Date getUpdated() {		return updated;	}	public void setUpdated(Date updated) {		this.updated = updated;	}	public Boolean getDataStatus() {		return dataStatus;	}	public void setDataStatus(Boolean dataStatus) {		this.dataStatus = dataStatus;	}	@Override	public String toString() {		final StringBuilder sb = new StringBuilder("Role{");		sb.append("id=").append(id);		sb.append(", roleName='").append(roleName).append('\'');		sb.append(", roleMemo='").append(roleMemo).append('\'');		sb.append(", isSystem=").append(isSystem);		sb.append(", created=").append(created);		sb.append(", updated=").append(updated);		sb.append(", dataStatus=").append(dataStatus);		sb.append('}');		return sb.toString();	}}